import { useContext } from 'react';
import { Link } from 'react-router-dom';
import { CartContext } from '../context/CartContext';

const Product = ({ product }) => {
  const { addToCart } = useContext(CartContext);

  const handleAddToCart = (e) => {
    e.preventDefault(); // Evita navegação ao clicar no botão
    addToCart(product);
  };

  return (
    <div className="product border rounded-lg overflow-hidden shadow-md hover:shadow-lg transition-shadow">
      <Link to={`/product/${product.id}`} className="block">
        <img 
          src={product.image} 
          alt={product.name} 
          className="w-full h-48 object-cover"
        />
        <div className="p-4">
          <h3 className="font-semibold text-lg">{product.name}</h3>
          <p className="text-gray-800 font-bold">R$ {product.price.toFixed(2)}</p>
        </div>
      </Link>
      <button 
        onClick={handleAddToCart}
        className="w-full bg-blue-500 text-white py-2 hover:bg-blue-600 transition-colors"
      >
        Adicionar ao Carrinho
      </button>
    </div>
  );
};

export default Product;
